#1. 访问页面获取到百度热榜页面新闻链接
#2. 通过访问链接获取到详细数据
    #2.1 通过分析链接 确定是视频新闻还是文章新闻，这边先爬取文章新闻
    # 获取详情页面的数据，可以根据headType来判断是文章还是视频，其中还有其他新闻网的数据

import scrapy
import re
import json
import unicodedata # 去除uncode 的空白字符
from datetime import datetime, timedelta
from baiduHOT.items import BaiduhotItem


class BaiduSpider(scrapy.Spider):
    name = 'baidu'
    # allowed_domains = ['top.baidu.com']
    start_urls = ['https://top.baidu.com/board?tab=realtime']
    

    #! 1. 获取百度热榜新闻数据
    def parse(self, response):
        dataReg = re.compile(r'<!--s-data:(?P<data>.*?)--> <div .*?', re.S)
        # print(dataReg.search(response.text).group('data'))
        data_dic = json.loads(dataReg.search(response.text).group('data'))
        data = data_dic['data']['cards'][0] # 这个是百度的数据结构
        
        #lamb 1.1 将数据进行拆分组装
        #? thumbnail 略缩图，desc简介，sbelong 状态，title标题
        if data['component'] == 'hotList':
            data = data['content']
            for list in data:
                thum = list['img']
                desc = list['desc']
                title = list['word']
                sbelong = self.belong_state(int(list['hotTag']))
                eyeNum = list['hotScore'] # 热度
                item = {"eyeNum":eyeNum,"sbelong":sbelong,"title":title,"desc":desc,"thum":thum}
                # hotTag 0 第一条就是百度新闻，有资讯。1 有事情脉络并且还有分类 第一条为百度新闻，有资讯，
                #2.  3. 第一条为百度视频新闻也可能是直播得看第二页的数据 有资讯
                #4.有事情脉络无分类 第一条为百度新闻，有资讯，  5. 像微博一样的新闻，无资讯
                hotTag = list['hotTag'] 
                # 请求二级页面
                if title == "尹某某投放异物后观察女生有无喝下":
                    yield scrapy.Request(url=list['url'], callback=self.parse_search_new,meta={"hotTag":hotTag,"item":item})
                    return

    #! 2. 通过链接访问到的对应新闻的结果列表,第二级页面
    def parse_search_new(self, response):
        hotTag = response.meta['hotTag']
        #todo 2.1 新闻简介
        baidu_new_body_reg = re.compile(r'<div class="pos-wrapper_22SLD "><!--s-data:(?P<data>.*?)--><div class="content-wrapper_2KXRm">', re.S)
        #todo 2.2 新闻资讯 三个相似新闻，不属于百度的 ,这里可能含有多项 所以不能使用search 选取第一项了
        baidu_new_similar = re.compile(r'<div><!--s-data:(?P<data>.*?)--><div>')
        if baidu_new_body_reg.search(response.text) == None:
            return
        new_body_data = json.loads(baidu_new_body_reg.search(response.text).group('data'))

        info_list = baidu_new_similar.findall(response.text)
        new_similar = {} #资讯信息
        for info in info_list:
            if '"titleAriaLabel":" 资讯 "' in info:
                new_similar = json.loads(info)
                break
        #? 2.1.1
        # 必须headType=1 为图片文字新闻，有略缩图，新闻内容可能有图片，一定有文字
        # headType = 4 为视频新闻，一定有视频，不一定有文字 
        # headType = 5 为直播
        if int(hotTag) in [0,1,3,4]: # 第一条为百度文章新闻，有资讯
            #? cbelong 爬取的是哪网站的， rbelong 源属于哪个 ,cauthor 爬取的新闻发布者，time 新闻本身的时间，
            cbelong = "百度新闻"
            rbelong = new_body_data['normalProps']['link']
            headType = new_body_data['normalProps']['headType']
            item = {**response.meta['item'],"cbelong":cbelong,"rbelong":rbelong,"headType":rbelong}
            
        

        #? 2.2.1 simila类似新闻
        similar = []
        if new_similar['dispTitle'].strip() == '资讯': # 资源对号入座
            if new_similar['isGroup'] == True:
                for list in new_similar['renderList']:
                    ele ={
                            "url":list['subTitleUrl'],
                            "title":list['subTitle'],
                            "belong":list['siteName'],
                            "time":self.time_timeStemp(list['postTimeNew']),
                            "desc":self.time_timeStemp(list['subAbs'])
                        }
                    similar.append(ele)
                item = {**item,"similar":similar}    
        #? cauthor 爬取的新闻发布者，time 新闻本身的时间，
        if int(headType) == 1:
            # cauthor = new_body_data['sourceName']
            # time = self.time_timeStemp(new_body_data['sourcePublishAt'])
            
            yield scrapy.Request(url=item['rbelong'], callback=self.parse_content_three)
        elif int(headType) == 4:
            pass

    #! 3.1 通过链接访问三级页面，详情页面
    def parse_content_three(self, response):
        print(response.url)
        data_reg = re.compile(r'window.jsonData = (?P<data>.*?)window.firstScreenTime = Date.now();', re.S)
        data = data_reg.search(response.text).group('data')
        print(data)




    #lamb 1.2 确定新闻状态        
    def belong_state(self,sbelong):
        baidu_state = ['',1,2,3,4,5] # "",新,商,热,沸,爆
        if sbelong == 0:
            return ""
        elif sbelong == 1:
            return 2
        elif sbelong == 2:
            return 7
        elif sbelong == 3:
            return 1
        elif sbelong == 4:
            return 5
        elif sbelong == 5:
            return 6
        return "未知"

    #lamb 2.1 获取时间戳
    def time_timeStemp(self, time_str):
        now = datetime.now() #当前时间
        date = time_str   # 最终时间
        # 判断是否存在一二三等 re.match(r'前([\u4e00\u4e8c\u4e09\u56db\u4e94\u516d\u4e03\u516b\u4e5d\u5341]*?)天','前一天').group(1) 一。 \d+ 匹配数组
        try:
            if time_str in "前天"  :
                if len(time_str).len >= 2 :
                    date = datetime.timestamp(now + timedelta(days=-2))
                else: #昨天22:12
                    hours_minutes = time_str.split("前天")[1].split(":")
                    date = datetime.timestamp(now + timedelta(days=-2,hours=int(hours_minutes[0]),minutes=int(hours_minutes[1])))
            elif time_str in "昨天":
                if len(time_str).len >= 2 :
                    date = datetime.timestamp(now + timedelta(days=-1))
                else: #昨天22:12
                    hours_minutes = time_str.split("昨天")[1].split(":")
                    date = datetime.timestamp(now + timedelta(days=-1,hours=int(hours_minutes[0]),minutes=int(hours_minutes[1])))
            elif "小时前" in time_str:# 2小时前
                hours = re.match(r'([\d+]*?)小时前',time_str).group(1)
                date = datetime.timestamp(now + timedelta(hours=int(hours)))
            elif "分钟前" in time_str: #35分钟前
                minutes = re.match(r'([\d+]*?)分钟前',time_str).group(1)
                date = datetime.timestamp(now + timedelta(minutes=int(minutes)))
            return date
        except Exception as err:
            print(err)
            return date
            



